import React, { useState } from "react";
import fire from './auth/FireBaseAuth'

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("")
  // firebase 
  const  handleSubmit = (event) =>{
    event.preventDefault()
    console.log(email,password);
    fire.auth().signInWithEmailAndPassword(email,password).then(()=>{
      alert("Login success")
    }).catch((error)=>{
      alert("No user exist | ",error);
    })
  }

  const Logout = ()=>{
    fire.auth().signOut();
    alert("Log out Successfully")
  }



  return (

<div id="login" className="container">
  <div className="row-fluid">
    <div className="span12">
      <div className="login well well-small">
        <div className="center">
          <img src="http://placehold.it/250x100&text=Logo" alt="logo" /> 
        </div>
        <form action="/users/login"  className="login-form" id="UserLoginForm" method="post" accept-charset="utf-8">
          <div className="control-group">
            <div className="input-prepend">
              <span className="add-on"><i className="icon-user"></i></span>
              <input name="data[User][username]" required="required" placeholder="Username" maxlength="255" type="text" id="UserUsername" /> 
            </div>
          </div>
          <div className="control-group">
            <div className="input-prepend">
              <span className="add-on"><i className="icon-lock"></i></span>
              <input name="data[User][password]" required="required" placeholder="Password" type="password" id="UserPassword" /> 
            </div>
          </div>
          <div className="control-group">
            <label id="remember-me">
              <input type="checkbox" name="data[User][remember_me]" value="1" id="UserRememberMe" /> Remember Me?</label>
          </div>
          <div className="control-group">
            <input className="btn btn-primary btn-large btn-block" type="submit" value="Sign in" /> 
          </div>
        </form>
      </div>
    </div>  </div>
</div>
  );
}
