  }
  const Logout = ()=>{
    fire.auth().signOut();
    alert("Log out Successfully")
  }

  

  return (
    <>
    <div className="login-form">
    <form onSubmit={handleSubmit}>
        <h2 className="text-center">Log in</h2>       
        <div className="form-group">
            <input type="text" className="form-control" onChange={(e)=>setEmail(e.target.value)} placeholder="Username" required="required" />
        </div>
        <div className="form-group">
            <input type="password" className="form-control" onChange={(e)=>setPassword(e.target.value)} placeholder="Password" required="required" />
        </div>
        <div className="form-group">
            <button type="submit" className="btn btn-primary btn-block">Log in</button>
        </div>
        <div className="clearfix">
            <label className="pull-left checkbox-inline"><input type="checkbox" /> Remember me</label>
            <a href="#" className="pull-right">Forgot Password?</a>
        </div>        
    </form>
    <p className="text-center"><a href="#">Create an Account</a></p>
    
</div> </>
  );
  
}
